import styles from "./input.module.scss";

const Input = ({ label, error, ...rest }) => {
  return (
    <div className={Styles.Input}>
      <label>{label}</label>
      <input> </input>
      {error && (
        <div>
          <span className={styles.InputError}>{error}</span>
        </div>
      )}
    </div>
  );
};

export default Input;
