import { Table } from "react-bootstrap";
import _ from "lodash";
import { useEffect, useState } from "react";

const FileDisplay = ({
  libraryFiles,
  filesSelected,
  setFilesSelected,
  showCheckbox = true,
}) => {
  const [date, setDate] = useState("");
  const [fileName, setFileName] = useState("");
  const [file, setFile] = useState("");

  useEffect(() => {
    if (file) {
      setFileName(
        `${new Date(file.lastModified).toLocaleDateString().split(",")[0]}` +
          `${file.name}`
      );
      setDate(new Date(file.lastModified).toLocaleString());
    }
  }, [file]);

  function handleCheck(value) {
    let tempFileArray = [];
    tempFileArray = filesSelected;
    if (_.includes(tempFileArray, value)) {
      const index = tempFileArray.indexOf(value);
      tempFileArray.splice(index, 1);
      setFilesSelected(tempFileArray);
    } else {
      tempFileArray.push(value);
      setFilesSelected(tempFileArray);
    }
  }

  function handleUpload(event) {
    setFile(event.target.files[0]);
  }
  useEffect(() => {
    const uplodadedFile = { date: date, fileName: fileName };
    const currentFiles = filesSelected;
    currentFiles.push(uplodadedFile);
    setFilesSelected(currentFiles);
    console.log("files selected:", filesSelected);
  }, [date]);

  return (
    <div>
      <Table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>File Size</th>
            <th>Date Created</th>
          </tr>
        </thead>
        <tbody>
          {libraryFiles.map((val, index) => {
            return (
              <tr key={index}>
                <td>{val.name}</td>
                <td>{val.size}</td>
                <td>{val.date}</td>
                <td>
                  {showCheckbox && (
                    <input
                      id="defaultUnchecked"
                      type="checkbox"
                      data={val}
                      defaultChecked={false}
                      onChange={() => handleCheck(val, index)}
                    />
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <input type="file" onChange={handleUpload} />
    </div>
  );
};

export default FileDisplay;
