import { useEffect, useRef, useState } from "react";
import useOutsideClick from "../custom-hooks/useOutsideClick";
import _ from "lodash";
import axios from "axios";
import countries from "../public/countries.json";
import AutoComplete from "./AutoComplete";
import { Form } from "react-bootstrap";

const InputControl = ({ name, Label, placeholder }) => {
  const [documentRef, isVisible, setIsVisible] = useOutsideClick();
  const [errorMsg, setErrorMsg] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const ref = useRef();

  useEffect(() => {
    ref.current = _.debounce(processRequest, 300);
  }, []);

  function processRequest(searchValue) {
    axios
      .get(countries)
      .then((response) => {
        const countries = response.data;
        const result = countries.filter((country) =>
          country.toLowerCase().includes(searchValue.toLowerCase())
        );
        setSuggestions(result);
        result.length && setIsVisible(true);
        !result.length && setIsVisible(false);
        setErrorMsg("");
      })
      .catch((e) => setErrorMsg("error occured in retreiving JSOn File", e));
  }

  function handleSearch(event) {
    event.preventDefault();

    const { value } = event.target;
    setSearchTerm(value);
    ref.current(value);
  }

  function handleSuggestionClick(countryValue) {
    setSelectedCountry(countryValue);
    setIsVisible(false);
  }

  return (
    <Form.Group controlId="searchTerm">
      <Form.Label>{Label}</Form.Label>
      <Form.Control
        className="input-control"
        type="text"
        value={searchTerm}
        name={name}
        onChange={handleSearch}
        outocomplete="off"
        placeholder={placeholder}
      />
      <div ref={documentRef}>
        {isVisible && (
          <AutoComplete
            isVisible={isVisible}
            suggestions={suggestions}
            handleSuggestionClick={handleSuggestionClick}
          />
        )}
      </div>
      {selectedCountry && (
        <div className="selected-country">
          Your selected country: {selectedCountry}
        </div>
      )}
      {errorMsg && <p className="errorMsg">{errorMsg}</p>}
    </Form.Group>
  );
};

export default InputControl;
