import { Table } from "react-bootstrap";
import _ from "lodash";
import { useEffect, useState } from "react";

const FileDisplay = ({
  libraryFiles,
  filesSelected,
  setFilesSelected,
  showCheckbox = true,
  showUpload = false,
}) => {
  const [date, setDate] = useState("");
  const [fileName, setFileName] = useState();
  const [file, setFile] = useState("");
  cosnt[(allFiles, setAllFiles)] = useState(libraryFiles);

  useEffect(() => {
    if (file) {
      setFileName(
        `${new Date(file.lastModified).toLocaleDateString()}` + `${file.name}`
      );
      setDate(new Date(file.lastModified).toLocaleString().split(",")[0]);
    }
  }, [file]);

  function handleCheck(value) {
    let tempFileArray = [];
    tempFileArray = filesSelected;
    if (_.includes(tempFileArray, value)) {
      const index = tempFileArray.indexOf(value);
      tempFileArray.splice(index, 1);
      setFilesSelected(tempFileArray);
    } else {
      tempFileArray.push(value);
      setFilesSelected(tempFileArray);
    }
  }

  function handleUpload(event) {
    setFile(event.target.files[0]);
  }
  useEffect(() => {
    if (date) {
      const uplodadedFile = { date: date, fileName: fileName };
      const currentFiles = filesSelected;
      currentFiles.push(uplodadedFile);
      setFilesSelected(currentFiles);
      libraryFiles.push(uplodadedFile);
      console.log("files selected:", libraryFiles);
    }
  }, [date]);

  return (
    <div>
      <Table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>Date Created</th>
          </tr>
        </thead>
        <tbody>
          {allFiles.map((val, index) => {
            return (
              <tr key={index}>
                <td>{val.fileName}</td>
                <td>{val.date}</td>
                <td>
                  {showCheckbox && (
                    <input
                      id="defaultUnchecked"
                      type="checkbox"
                      data={val}
                      defaultChecked={false}
                      onChange={() => handleCheck(val, index)}
                    />
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
      {showUpload && <input type="file" onChange={handleUpload} />}
    </div>
  );
};

export default FileDisplay;
