import { Form, Button } from "react-bootstrap";
import { useEffect, useState } from "react";
import isValidInfo from "./validation";

{
  /*  name, address, email and phone number */
}
const UserInputControl = ({ step, setStep, setUserDetails }) => {
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [errors, setErrors] = useState({});

  function handleName(event) {
    event.preventDefault();
    const { value, name } = event.target;
    const errors = isValidInfo(vale, name);
    setErrors(errors);
    value && setName(value);
  }
  function handleAddress(event) {
    event.preventDefault();
    const { value, name } = event.target;
    value && setAddress(value);
  }
  function handleEmail(event) {
    event.preventDefault();
    const { value, name } = event.target;
    value && setEmail(value);
  }
  function handlePhoneNumber(event) {
    event.preventDefault();
    const { value, name } = event.target;
    value && setPhoneNumber(value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    console.log("event.target: ", event);
    let userDetails = [
      {
        name: name,
        address: address,
        email: email,
        phoneNumber: phoneNumber,
      },
    ];
    setUserDetails(userDetails);
    setStep(step + 1);
  }

  function handlePrev(event) {
    event.preventDefault();
    setUserDetails("");
    setStep(step - 1);
  }

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="name">
        <Form.Label>Name</Form.Label>
        <Form.Control
          type="name"
          onChange={handleName}
          name="name"
          value={name}
          placeholder="enter your name"
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="address">
        <Form.Label>Address</Form.Label>
        <Form.Control
          className="user-details"
          type="text"
          name="address"
          onChange={handleAddress}
          value={address}
          placeholder="enter your Address"
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="email">
        <Form.Label>email address</Form.Label>
        <Form.Control
          className="user-details"
          type="email"
          name="email"
          onChange={handleEmail}
          value={email}
          placeholder="enter your email address"
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="phoneNumber">
        <Form.Label>phone number</Form.Label>
        <Form.Control
          className="user-details"
          type="number"
          name="name"
          onChange={handlePhoneNumber}
          value={phoneNumber}
          placeholder="enter your phone number"
        />
      </Form.Group>
      <div>
        <Button variant="danger" type="submit" onClick={handlePrev}>
          Previous
        </Button>
        <Button variant="primary" type="submit">
          Next
        </Button>
      </div>
    </Form>
  );
};

export default UserInputControl;
