import { useState } from "react";
import FilesInDisplay from "./FilesInDisplay";
import InputControl from "./InputControl";
import { Form } from "react-bootstrap";
const LibraryModal = ({ onSubmit, page }) => {
  const [currentPage, setCurrentPage] = useState(1);
  // const [nextPage, setNetPage] = useState(2);
  // const [previousPage, setPreviousPage] = useState();

  function nextPage() {
    if (currentPage + 1 < 3) {
      setCurrentPage(currentPage + 1);
    }
  }
  function previousPage() {
    if (currentPage - 1 > 0) {
      setCurrentPage(currentPage - 1);
    }
  }

  return (
    <>
      <h3> My Wizard</h3>
      <p>heyo</p>
      <div>
        {currentPage === 1 && <FilesInDisplay onSubmit={nextPage} />}
        {currentPage === 2 && (
          <Form>
            <InputControl
              name="country"
              label="Enter Country"
              placeholder="type a country name"
              previousPage={previousPage}
              onSubmit={nextPage}
            />
          </Form>
        )}
      </div>
    </>
  );
};

export default LibraryModal;
