import {Table} from "react-bootstrap";
import _ from "lodash"

const FileDisplay = ({libraryFiles, filesSelected,setFilesSelected, showCheckbox = true}) => {

      function handleCheck(value) {
    let tempFileArray = [];
    tempFileArray = filesSelected;
    if (_.includes(tempFileArray, value)) {
      const index = tempFileArray.indexOf(value);
      tempFileArray.splice(index, 1);
      setFilesSelected(tempFileArray);
    } else {
      tempFileArray.push(value);
      setFilesSelected(tempFileArray);
    }
  }
    return(
        <div>
              <Table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>File Size</th>
            <th>Date Created</th>
          </tr>
        </thead>
        <tbody>
          {libraryFiles.map((val, index) => {
            return (
              <tr key={index}>
                <td>{val.name}</td>
                <td>{val.size}</td>
                <td>{val.date}</td>
                <td>
                  {showCheckbox && <input
                    id="defaultUnchecked"
                    type="checkbox"
                    data={val}
                    defaultChecked={false}
                    onChange={() => handleCheck(val, index)}
                  />}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
            <input type="file" onChange={handleUpload} />
            </div>
    )
}

export default FileDisplay;
