import { useReducer, useState } from "react";
import FormSteps from "./form-steps";
import "./index.css";
import { UserReducer, DefaultUser } from "./user-reducer";

const ManageFiles = () => {
  let [step, setStep] = useState(0);
  let [files, setFiles] = useState();
  let [userDetails, setUserDetails] = useReducer(UserReducer, DefaultUser);
  let steps = FormSteps(
    step,
    setStep,
    userDetails,
    setUserDetails,
    files,
    setFiles
  );

  return (
    <div>
      <h1> Upload or choose a file</h1>
      <h4>
        Step {step + 1}/{steps.length}
      </h4>
      <div className="wizard">{steps[`${step}`].content}</div>
    </div>
  );
};

export default ManageFiles;
