import { Table } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import _ from "lodash";

const FilesInDisplay = () => {
  const [libraryFiles, setLibraryFiles] = useState([]);
  const [error, setError] = useState("");
  const [files, setFiles] = useState([]);
  const [fileSeletecStatus, setFileSelectedStatus] = useState(false);
  let filesSelected = [];
  useEffect(() => {
    axios
      .get("./fileDetails.json")
      .then((response) => {
        setLibraryFiles(response.data);
      })
      .catch((error) => {
        setError(error);
      });
  }, []);

  function handleCheck(value) {
    if (!fileSeletecStatus) {
      filesSelected.push(value);
      setFileSelectedStatus(true);
      console.log(
        "filesSelected handleCheck function when its CHECKED: ",
        filesSelected
      );
    } else {
      filesSelected.pop(value);
      setFileSelectedStatus(false);
      console.log(
        "filesSelected handleCheck function when its UNchecked: ",
        filesSelected
      );
    }
  }

  useEffect(() => {
    console.log("filesSelected", filesSelected);
    setFiles(filesSelected);
    console.log("files", files);
  }, [fileSeletecStatus]);

  return (
    <>
      <h1>Library Files</h1>
      <Table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>File Size</th>
            <th>Date Created</th>
          </tr>
        </thead>
        <tbody>
          {libraryFiles.map((val, index) => {
            return (
              <tr key={index}>
                <td>{val.name}</td>
                <td>{val.size}</td>
                <td>{val.date}</td>
                <td>
                  <input
                    id="defaultUnchecked"
                    type="checkbox"
                    data={val}
                    defaultChecked={false}
                    state={false}
                    onChange={() => handleCheck(val)}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </>
  );
};

export default FilesInDisplay;
