import { Table } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import _ from "lodash";

const FilesInDisplay = () => {
  const [libraryFiles, setLibraryFiles] = useState([]);
  const [error, setError] = useState("");
  const [files, setFiles] = useState();
  const [filesSelected, setFilesSelected] = useState([]);
  useEffect(() => {
    axios
      .get("./fileDetails.json")
      .then((response) => {
        setLibraryFiles(response.data);
      })
      .catch((error) => {
        setError(error);
      });
  }, []);

  function handleCheck(value) {
    let tempFileArray = [];
    tempFileArray = filesSelected;
    if (_.includes(tempFileArray, value)) {
      const index = tempFileArray.indexOf(value);
      tempFileArray.splice(index, 1);
      //   tempFileArray = tempFileArray.filter((item) => _.includes(value, item));
      setFilesSelected(tempFileArray);
    } else {
      tempFileArray.push(value);
      setFilesSelected(tempFileArray);
    }
  }

  useEffect(() => {
    console.log("filesSelected:::: the Array Object ", filesSelected);
    setFiles(filesSelected);
    console.log("files::::", files);
  }, [filesSelected]);

  return (
    <>
      <h1>Library Files</h1>
      <Table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>File Size</th>
            <th>Date Created</th>
          </tr>
        </thead>
        <tbody>
          {libraryFiles.map((val, index) => {
            return (
              <tr key={index}>
                <td>{val.name}</td>
                <td>{val.size}</td>
                <td>{val.date}</td>
                <td>
                  <input
                    id="defaultUnchecked"
                    type="checkbox"
                    data={val}
                    defaultChecked={false}
                    state={false}
                    onChange={() => handleCheck(val, index)}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </>
  );
};

export default FilesInDisplay;
