const isValidInfo = (value, name) => {
  let errors = {};
  if (!value) {
    errors.name = "Name is required";
  } else if (name === "name" && !/^[A-Z]$/.test(value)) {
    errors.name = "name is not valid";
  }
  console.log("errors: ", errors);
};

export default isValidInfo;
