import { Table, Button } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import _ from "lodash";
import TYable from "./table";

const FilesInDisplay = ({ step, setStep, setFiles }) => {
  const [libraryFiles, setLibraryFiles] = useState([]);
  const [error, setError] = useState("");
  const [filesSelected, setFilesSelected] = useState([]);
  useEffect(() => {
    axios
      .get("./fileDetails.json")
      .then((response) => {
        setLibraryFiles(response.data);
      })
      .catch((error) => {
        setError(error);
      });
  }, []);

  function handleCheck(value) {
    let tempFileArray = [];
    tempFileArray = filesSelected;
    if (_.includes(tempFileArray, value)) {
      const index = tempFileArray.indexOf(value);
      tempFileArray.splice(index, 1);
      //   tempFileArray = tempFileArray.filter((item) => _.includes(value, item));
      setFilesSelected(tempFileArray);
    } else {
      tempFileArray.push(value);
      setFilesSelected(tempFileArray);
    }
  }

  function onSubmitHandler(event) {
    event.preventDefault();
    console.log("selected files: ", filesSelected);
    setFiles(filesSelected);
    setStep(step + 1);
  }

  return (
    <>
      <h1>Library Files</h1>
      <Table libraryFiles={libraryFiles} />
      <Button
        type="submit"
        style={{ marginLeft: "18pc" }}
        onClick={onSubmitHandler}
      >
        Proceed
      </Button>
    </>
  );
};

export default FilesInDisplay;
