import React from "react";
import FilesInDisplay from "../FilesInDisplay";
import UserInputControl from "../UserInputControl";
import ConfirmatoryPage from "./confirmatory-page";
const FormSteps = (
  step,
  setStep,
  files,
  setFiles,
  userDetails,
  setUserDetails
) => [
  {
    title: "Choose a file",
    content: (
      <FilesInDisplay setStep={setStep} step={step} setFiles={setFiles} />
    ),
  },
  {
    title: "enter Use Details",
    content: (
      <UserInputControl
        setStep={setStep}
        step={step}
        setUserDetails={setUserDetails}
        files={files}
      />
    ),
  },
  {
    title: "Confirmatory Page",
    content: (
      <ConfirmatoryPage
        step={step}
        setStep={setStep}
        userDetails={userDetails}
        files={files}
      />
    ),
  },
];
export default FormSteps;
