import { useEffect, useState } from "react";
import styles from "./user-details.module.scss";
import Input from "../components/input";
import { isValid } from "./validation";
import { Button } from "react-bootstrap";

// {
//   /*  name, address, email and phone number */
// }

const UserInputControl = ({ step, setStep, userDetails, setUserDetails }) => {
  const [errors, setErrors] = useState({});
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");

  //onChange
  const handleUser = ({ target }) => {
    target.name === "fullName" && setName(target.value);
    target.name === "address" && setAddress(target.value);
    target.name === "email" && setEmail(target.value);
    target.name === "phoneNumber" && setPhoneNumber(target.value);
  };

  const updateState = (e) => {
    e.preventDefault();
    setUserDetails({
      name: name,
      address: address,
      email: email,
      phoneNumber: phoneNumber,
    });
    handleContinue(e);
  };
  const handleContinue = (e) => {
    e.preventDefault();
    setUserDetails({
      name: name,
      address: address,
      email: email,
      phoneNumber: phoneNumber,
    });
    const error = isValid(userDetails);
    setErrors(error);
    if (Object.keys(errors).length > 0) return;
    setStep(step + 1);
  };

  const handlePrev = (e) => {
    e.preventDefault();
    setUserDetails("");
    setStep(step - 1);
  };

  return (
    <form onSubmit={handleContinue} className={styles.UserForm}>
      <Input
        type="text"
        name="fullName"
        value={name}
        label="Full Name"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="text"
        name="address"
        value={address}
        label="Address"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="email"
        name="email"
        value={email}
        label="Email"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="number"
        name="phoneNumber"
        value={phoneNumber}
        label="Phone Number"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <div>
        <Button variant="danger" type="submit" onClick={handlePrev}>
          Previous
        </Button>
        <Button variant="primary" type="submit" onClick={updateState}>
          Next
        </Button>
      </div>
    </form>
  );
};

export default UserInputControl;
