import { useEffect, useState } from "react";
import styles from "./user-details.module.scss";
import Input from "../components/input";
import { isValid } from "./validation";
import { Button } from "react-bootstrap";

// {
//   /*  name, address, email and phone number */
// }

const UserInputControl = ({ step, setStep, userDetails, setUserDetails }) => {
  const [errors, setErrors] = useState({});
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");

  //onChange
  const handleUser = ({ target }) => {
    target.name === "fullName" && setName(target.value);
    target.name === "address" && setAddress(target.value);
    target.name === "email" && setEmail(target.value);
    target.name === "phoneNumber" && setPhoneNumber(target.value);
  };

  const handlePrev = (e) => {
    e.preventDefault();
    setUserDetails("");
    setStep(step - 1);
  };

  function handleContinue(e) {
    e.preventDefault();
    setUserDetails({
      name: name,
      address: address,
      email: email,
      phoneNumber: phoneNumber,
    });
    const error = isValid(userDetails);
    setErrors(error);
    if (Object.keys(errors).length > 0) return;
    setStep(step + 1);
  }
  return (
    <form onSubmit={handleContinue} className={styles.UserForm}>
      <Input
        type="text"
        name="fullName"
        value={name}
        label="Full Name"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="text"
        name="address"
        value={address}
        label="Address"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="email"
        name="email"
        value={email}
        label="Email"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <Input
        type="number"
        name="phoneNumber"
        value={phoneNumber}
        label="Phone Number"
        onChange={(e) => handleUser(e)}
        error={errors.name}
      />
      <div>
        <Button variant="danger" type="submit" onClick={handlePrev}>
          Previous
        </Button>
        <Button variant="primary" type="submit">
          Next
        </Button>
      </div>
    </form>
    // <Form onSubmit={handleContinue} className={Styles.RegisterForm}>
    //   <Input />
    //   <Form.Group className="mb-3" controlId="name">
    //     <Form.Label>Name</Form.Label>
    //     <Form.Control
    //       type="name"
    //       onChange={handleName}
    //       name="name"
    //       value={name}
    //       placeholder="enter your name"
    //     />
    //   </Form.Group>
    //   <Form.Group className="mb-3" controlId="address">
    //     <Form.Label>Address</Form.Label>
    //     <Form.Control
    //       className="user-details"
    //       type="text"
    //       name="address"
    //       onChange={handleAddress}
    //       value={address}
    //       placeholder="enter your Address"
    //     />
    //   </Form.Group>
    //   <Form.Group className="mb-3" controlId="email">
    //     <Form.Label>email address</Form.Label>
    //     <Form.Control
    //       className="user-details"
    //       type="email"
    //       name="email"
    //       onChange={handleEmail}
    //       value={email}
    //       placeholder="enter your email address"
    //     />
    //   </Form.Group>
    //   <Form.Group className="mb-3" controlId="phoneNumber">
    //     <Form.Label>phone number</Form.Label>
    //     <Form.Control
    //       className="user-details"
    //       type="number"
    //       name="name"
    //       onChange={handlePhoneNumber}
    //       value={phoneNumber}
    //       placeholder="enter your phone number"
    //     />
    //   </Form.Group>

    // </Form>
  );
};

export default UserInputControl;
