import { Table, Button } from "react-bootstrap";
import axios from "axios";
import { useEffect, useState } from "react";
import _ from "lodash";
import FileDisplay from "./table";

const FilesInDisplay = ({ step, setStep, setFiles }) => {
  const [libraryFiles, setLibraryFiles] = useState([]);
  const [error, setError] = useState("");
  const [filesSelected, setFilesSelected] = useState([]);

  useEffect(() => {
    axios
      .get("./fileDetails.json")
      .then((response) => {
        setLibraryFiles(response.data);
      })
      .catch((error) => {
        setError(error);
      });
  }, []);

  function onSubmitHandler(event) {
    event.preventDefault();
    setFiles(filesSelected);
    setStep(step + 1);
  }
  function onCancelHandler(event) {
    event.preventDefault();
    setFiles("");
  }

  return (
    <>
      <h1>Library Files</h1>
      <FileDisplay
        libraryFiles={libraryFiles}
        setFilesSelected={setFilesSelected}
        filesSelected={filesSelected}
        showUpload={true}
      />
      <div styles={(display = inline)}>
        <Button
          type="submit"
          style={{ marginLeft: "18pc" }}
          onClick={onCancelHandler}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          style={{ marginLeft: "18pc" }}
          onClick={onSubmitHandler}
        >
          Proceed
        </Button>
      </div>
    </>
  );
};

export default FilesInDisplay;
