import { useState } from "react";

function FileUpload() {
  const [file, setFile] = useState("");

  function handleUpload(event) {
    setFile(event.target.files[0]);
  }

  return <input type="file" onChange={handleUpload} />;
  <p>file: {file.name}</p>;
}

export default FileUpload;
